// Simple test to check if the avatar route file is syntactically correct
const fs = require('fs');
const path = require('path');

try {
  const filePath = path.join(__dirname, 'src/app/api/companies/[id]/avatar/route.ts');
  const content = fs.readFileSync(filePath, 'utf8');
  
  // Check for the problematic patterns
  if (content.includes(':9:') || content.includes(':48:')) {
    console.error('‚ùå Found problematic line number patterns in avatar route');
    process.exit(1);
  }
  
  // Check for proper function structure
  if (!content.includes('export async function POST') || !content.includes('export async function DELETE')) {
    console.error('‚ùå Missing required export functions in avatar route');
    process.exit(1);
  }
  
  console.log('‚úÖ Avatar route file syntax looks good');
  console.log('‚úÖ Build should now work correctly');
  
} catch (error) {
  console.error('‚ùå Error checking avatar route file:', error.message);
  process.exit(1);
}

