+// Script to add dynamic export to API routes that use getCurrentUser or request.headers
+const fs = require('fs');
+const path = require('path');
+
+const apiDir = path.join(__dirname, 'src/app/api');
+
+function findTsFiles(dir) {
+  const files = [];
+  const items = fs.readdirSync(dir);
+  
+  for (const item of items) {
+    const fullPath = path.join(dir, item);
+    const stat = fs.statSync(fullPath);
+    
+    if (stat.isDirectory()) {
+      files.push(...findTsFiles(fullPath));
+    } else if (item === 'route.ts') {
+      files.push(fullPath);
+    }
+  }
+  
+  return files;
+}
+
+function needsDynamicExport(content) {
+  return (
+    (content.includes('getCurrentUser') || content.includes('request.headers')) &&
+    !content.includes('export const dynamic = \'force-dynamic\'')
+  );
+}
+
+function addDynamicExport(content) {
+  // Find the last import statement
+  const lines = content.split('\n');
+  let lastImportIndex = -1;
+  
+  for (let i = 0; i < lines.length; i++) {
+    if (lines[i].trim().startsWith('import ')) {
+      lastImportIndex = i;
+    }
+  }
+  
+  if (lastImportIndex !== -1) {
+    lines.splice(lastImportIndex + 1, 0, '', '// Force dynamic rendering for this route', 'export const dynamic = \'force-dynamic\';');
+  }
+  
+  return lines.join('\n');
+}
+
+const routeFiles = findTsFiles(apiDir);
+let fixedCount = 0;
+
+console.log(`Found ${routeFiles.length} route files`);
+
+for (const file of routeFiles) {
+  try {
+    const content = fs.readFileSync(file, 'utf8');
+    
+    if (needsDynamicExport(content)) {
+      const fixedContent = addDynamicExport(content);
+      fs.writeFileSync(file, fixedContent);
+      console.log(`‚úÖ Fixed: ${path.relative(__dirname, file)}`);
+      fixedCount++;
+    }
+  } catch (error) {
+    console.error(`‚ùå Error processing ${file}:`, error.message);
+  }
+}
+
+console.log(`\nüéâ Fixed ${fixedCount} route files`);
+console.log('‚úÖ All API routes that use getCurrentUser or request.headers now have dynamic export');

