;%0Aimport %7B useToast %7D from '@/hooks/use-toast';%0Aimport %7B Avatar %7D from '@/components/Avatar';%0Aimport %7B AvatarUploader %7D from '@/components/avatar-uploader';%0Aimport EnhancedWorkerSelector from '@/components/EnhancedWorkerSelector';%0Aimport %7B useUsers %7D from '@/hooks/use-api';%0A%0Aexport default function TestAvatarsPage() %7B%0A  const %7B toast %7D = useToast();%0A  const %7B data: usersResponse, isLoading %7D = useUsers(%7B fetchAll: true %7D);%0A  const users = usersResponse?.users %7C%7C %5B%5D;%0A  const %5BselectedUserId, setSelectedUserId%5D = useState%3Cstring %7C null%3E(null);%0A  const %5BtestResults, setTestResults%5D = useState%3Cany%3E(null);%0A  const %5BisRefreshing, setIsRefreshing%5D = useState(false);%0A%0A  const selectedUser = users.find(u =%3E u.id === selectedUserId);%0A%0A  const refreshAvatars = async () =%3E %7B%0A    setIsRefreshing(true);%0A    try %7B%0A      // Force refresh all avatar components%0A      window.dispatchEvent(new CustomEvent('avatarUpdated', %7B%0A        detail: %7B userId: 'all', deleted: false %7D%0A      %7D));%0A      %0A      toast(%7B%0A        title: 'Success',%0A        description: 'Avatar cache refreshed for all users'%0A      %7D);%0A    %7D catch (error) %7B%0A      toast(%7B%0A        title: 'Error',%0A        description: 'Failed to refresh avatars',%0A        variant: 'destructive'%0A      %7D);%0A    %7D finally %7B%0A      setIsRefreshing(false);%0A    %7D%0A  %7D;%0A%0A  const testAvatarAPI = async (userId: string) =%3E %7B%0A    try %7B%0A      const results = %7B%0A        userId,%0A        userName: users.find(u =%3E u.id === userId)?.name %7C%7C 'Unknown',%0A        tests: %7B%0A          avatarExists: false,%0A          avatarLoads: false,%0A          apiResponds: false,%0A          cacheWorks: false%0A        %7D,%0A        errors: %5B%5D as string%5B%5D%0A      %7D;%0A%0A      // Test 1: Check if avatar API responds%0A      try %7B%0A        const response = await fetch(%60/api/users/$%7BuserId%7D/avatar/image%60, %7B method: 'HEAD' %7D);%0A        results.tests.apiResponds = true;%0A        results.tests.avatarExists = response.ok;%0A      %7D catch (error) %7B%0A        results.errors.push(%60API test failed: $%7Berror instanceof Error ? error.message : 'Unknown error'%7D%60);%0A      %7D%0A%0A      // Test 2: Try to load avatar image%0A      if (results.tests.avatarExists) %7B%0A        try %7B%0A          const img = new Image();%0A          await new Promise((resolve, reject) =%3E %7B%0A            img.onload = resolve;%0A            img.onerror = reject;%0A            img.src = %60/api/users/$%7BuserId%7D/avatar/image?t=$%7BDate.now()%7D%60;%0A          %7D);%0A          results.tests.avatarLoads = true;%0A        %7D catch (error) %7B%0A          results.errors.push(%60Image load test failed: $%7Berror instanceof Error ? error.message : 'Unknown error'%7D%60);%0A        %7D%0A      %7D%0A%0A      // Test 3: Cache busting test%0A      try %7B%0A        const url1 = %60/api/users/$%7BuserId%7D/avatar/image?t=$%7BDate.now()%7D%60;%0A        const url2 = %60/api/users/$%7BuserId%7D/avatar/image?t=$%7BDate.now() + 1000%7D%60;%0A        results.tests.cacheWorks = url1 !== url2;%0A      %7D catch (error) %7B%0A        results.errors.push(%60Cache test failed: $%7Berror instanceof Error ? error.message : 'Unknown error'%7D%60);%0A      %7D%0A%0A      setTestResults(results);%0A      %0A      toast(%7B%0A        title: 'Test Complete',%0A        description: %60Avatar tests completed for $%7Bresults.userName%7D%60%0A      %7D);%0A%0A    %7D catch (error) %7B%0A      toast(%7B%0A        title: 'Test Failed',%0A        description: error instanceof Error ? error.message : 'Unknown error',%0A        variant: 'destructive'%0A      %7D);%0A    %7D%0A  %7D;%0A%0A  if (isLoading) %7B%0A    return (%0A      %3Cdiv className=%22container mx-auto p-6%22%3E%0A        %3CCard%3E%0A          %3CCardHeader%3E%0A            %3CCardTitle className=%22flex items-center gap-2%22%3E%0A              %3CRefreshCw className=%22h-5 w-5 animate-spin%22 /%3E%0A              Loading Users...%0A            %3C/CardTitle%3E%0A          %3C/CardHeader%3E%0A          %3CCardContent%3E%0A            %3Cdiv className=%22flex items-center justify-center p-8%22%3E%0A              %3Cdiv className=%22animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600%22 /%3E%0A            %3C/div%3E%0A          %3C/CardContent%3E%0A        %3C/Card%3E%0A      %3C/div%3E%0A    );%0A  %7D%0A%0A  return (%0A    %3Cdiv className=%22container mx-auto p-6 space-y-6%22%3E%0A      %3Cdiv className=%22flex items-center justify-between%22%3E%0A        %3Cdiv%3E%0A          %3Ch1 className=%22text-3xl font-bold%22%3EAvatar System Test%3C/h1%3E%0A          %3Cp className=%22text-gray-600 mt-2%22%3E%0A            Test avatar display and functionality across different components%0A          %3C/p%3E%0A        %3C/div%3E%0A        %3CBadge variant=%22destructive%22 className=%22flex items-center gap-1%22%3E%0A          %3CAlertTriangle className=%22h-3 w-3%22 /%3E%0A          Admin Only%0A        %3C/Badge%3E%0A      %3C/div%3E%0A%0A      %7B/* User Selection */%7D%0A      %3CCard%3E%0A        %3CCardHeader%3E%0A          %3CCardTitle className=%22flex items-center gap-2%22%3E%0A            %3CUsers className=%22h-5 w-5%22 /%3E%0A            Select User for Testing%0A          %3C/CardTitle%3E%0A        %3C/CardHeader%3E%0A        %3CCardContent%3E%0A          %3Cdiv className=%22space-y-4%22%3E%0A            %3Cdiv className=%22min-w-%5B300px%5D%22%3E%0A              %3CEnhancedWorkerSelector%0A                users=%7Busers%7D%0A                selectedUserId=%7BselectedUserId%7D%0A                onChange=%7BsetSelectedUserId%7D%0A                placeholder=%22Select a user to test avatars%22%0A              /%3E%0A            %3C/div%3E%0A            %0A            %3Cdiv className=%22flex gap-2%22%3E%0A              %3CButton%0A                onClick=%7BrefreshAvatars%7D%0A                disabled=%7BisRefreshing%7D%0A                variant=%22outline%22%0A                className=%22flex items-center gap-2%22%0A              %3E%0A                %3CRefreshCw className=%7B%60h-4 w-4 $%7BisRefreshing ? 'animate-spin' : ''%7D%60%7D /%3E%0A                Refresh All Avatars%0A              %3C/Button%3E%0A              %0A              %7BselectedUserId && (%0A                %3CButton%0A                  onClick=%7B() =%3E testAvatarAPI(selectedUserId)%7D%0A                  className=%22flex items-center gap-2%22%0A                %3E%0A                  %3CEye className=%22h-4 w-4%22 /%3E%0A                  Test Avatar API%0A                %3C/Button%3E%0A              )%7D%0A            %3C/div%3E%0A          %3C/div%3E%0A        %3C/CardContent%3E%0A      %3C/Card%3E%0A%0A      %7B/* Avatar Display Tests */%7D%0A      %7BselectedUser && (%0A        %3CCard%3E%0A          %3CCardHeader%3E%0A            %3CCardTitle%3EAvatar Display Test - %7BselectedUser.name%7D%3C/CardTitle%3E%0A          %3C/CardHeader%3E%0A          %3CCardContent%3E%0A            %3Cdiv className=%22grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6%22%3E%0A              %7B/* Standard Avatar */%7D%0A              %3Cdiv className=%22text-center space-y-2%22%3E%0A                %3Ch3 className=%22font-medium%22%3EStandard Avatar%3C/h3%3E%0A                %3CAvatar%0A                  src=%7BselectedUser.avatarUrl%7D%0A                  name=%7BselectedUser.name %7C%7C selectedUser.email %7C%7C 'U'%7D%0A                  userId=%7BselectedUser.id%7D%0A                  size=%22lg%22%0A                  enableSmartCaching=%7Bfalse%7D%0A                /%3E%0A                %3Cp className=%22text-xs text-gray-500%22%3ENo smart caching%3C/p%3E%0A              %3C/div%3E%0A%0A              %7B/* Smart Cached Avatar */%7D%0A              %3Cdiv className=%22text-center space-y-2%22%3E%0A                %3Ch3 className=%22font-medium%22%3ESmart Cached Avatar%3C/h3%3E%0A                %3CAvatar%0A                  src=%7BselectedUser.avatarUrl%7D%0A                  name=%7BselectedUser.name %7C%7C selectedUser.email %7C%7C 'U'%7D%0A                  userId=%7BselectedUser.id%7D%0A                  size=%22lg%22%0A                  enableSmartCaching=%7Btrue%7D%0A                /%3E%0A                %3Cp className=%22text-xs text-gray-500%22%3EWith smart caching%3C/p%3E%0A              %3C/div%3E%0A%0A              %7B/* Avatar Uploader */%7D%0A              %3Cdiv className=%22text-center space-y-2%22%3E%0A                %3Ch3 className=%22font-medium%22%3EAvatar Uploader%3C/h3%3E%0A                %3CAvatarUploader%0A                  src=%7BselectedUser.avatarUrl%7D%0A                  fallback=%7BselectedUser.name?.split(' ').map(n =%3E n%5B0%5D).join('').toUpperCase().slice(0, 2) %7C%7C 'U'%7D%0A                  userId=%7BselectedUser.id%7D%0A                  size=%22lg%22%0A                  onUploadSuccess=%7B(url) =%3E %7B%0A                    toast(%7B%0A                      title: 'Success',%0A                      description: 'Avatar uploaded successfully'%0A                    %7D);%0A                  %7D%7D%0A                  onUploadError=%7B(error) =%3E %7B%0A                    toast(%7B%0A                      title: 'Error',%0A                      description: error,%0A                      variant: 'destructive'%0A                    %7D);%0A                  %7D%7D%0A                /%3E%0A                %3Cp className=%22text-xs text-gray-500%22%3EUpload functionality%3C/p%3E%0A              %3C/div%3E%0A%0A              %7B/* Different Sizes */%7D%0A              %3Cdiv className=%22text-center space-y-2%22%3E%0A                %3Ch3 className=%22font-medium%22%3ESize Variants%3C/h3%3E%0A                %3Cdiv className=%22flex items-center justify-center gap-2%22%3E%0A                  %3CAvatar%0A                    src=%7BselectedUser.avatarUrl%7D%0A    