import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/middleware';
import { prisma } from '@/lib/prisma';
import { UserRole } from '@prisma/client';

export const dynamic = 'force-dynamic';


export async function GET(request: NextRequest) {
  const user = await getCurrentUser(request);

  console.log('Timesheets API Debug:', {
    user: user ? { id: user.id, role: user.role, email: user.email } : null,
    userRole: user?.role,
    isAdmin: user?.role === UserRole.Admin,
    isCrewChief: user?.role === UserRole.CrewChief
  });

  const isAuthorized = user.role === UserRole.Admin || user.role === UserRole.CrewChief;
  if (!user || !isAuthorized) {
    console.log('Authorization failed:', { user: !!user, isAuthorized, userRole: user?.role });
    return NextResponse.json(
      { error: 'Authentication required', debug: { userRole: user?.role, isAuthorized } },
      { status: 401 }
    );
  }

  const { searchParams } = new URL(request.url);
  const shiftId = searchParams.get('shiftId');

  const where: any = {};

  // Only filter by shiftId if explicitly provided
  if (shiftId) {
    where.shiftId = shiftId;
  }

  if (user.role === UserRole.CrewChief) {
    // This logic needs to be adjusted based on how CrewChief is associated with a shift.
    // Assuming a crew chief is assigned to a shift via AssignedPersonnel
    where.shift = {
      assignedPersonnel: {
        some: {
          userId: user.id,
          // Assuming there's a way to identify the crew chief role here
        },
      },
    };
  }

  console.log('Query where clause:', where);

  try {
    const timesheets = await prisma.timesheet.findMany({
      where,
      include: {
        shift: {
          include: {
            job: {
              include: {
                company: {
                  select: {
                    id: true,
                    name: true,
                    company_logo_url: true,
                  },
                },
              },
            },
            assignedPersonnel: {
              include: {
                user: {
                  select: {
                    id: true,
                    name: true,
                    email: true,
                  },
                },
                timeEntries: true,
              },
            },
          },
        },
      },
      orderBy: {
        shift: {
          date: 'desc',
        },
      },
    });

    return NextResponse.json({
      success: true,
      timesheets,
    });
  } catch (error) {
    console.error('Error getting timesheets:', error);
    const errorMessage =
      error instanceof Error ? error.message : 'An unexpected error occurred';
    return NextResponse.json(
      { error: 'Internal server error', details: errorMessage },
      { status: 500 }
    );
  }
}

import { z } from 'zod';
import { TimesheetStatus } from '@prisma/client';

const createTimesheetSchema = z.object({
  shiftId: z.string().min(1, { message: 'Shift ID is required' }),
});

export async function POST(request: NextRequest) {
  const user = await getCurrentUser(request);
  const isAuthorized = user.role === UserRole.Admin || user.role === UserRole.CrewChief;
  if (!user || !isAuthorized) {
    return NextResponse.json(
      { error: 'Authentication required' },
      { status: 401 }
    );
  }

  try {
    const body = await request.json();
    const validation = createTimesheetSchema.safeParse(body);

    if (!validation.success) {
      return NextResponse.json(
        {
          error: 'Invalid request body',
          issues: validation.error.flatten().fieldErrors,
        },
        { status: 400 }
      );
    }

    const { shiftId } = validation.data;

    // Check if a timesheet for this shift already exists
    const existingTimesheet = await prisma.timesheet.findUnique({
      where: { shiftId },
    });

    if (existingTimesheet) {
      return NextResponse.json(
        { error: 'A timesheet for this shift already exists' },
        { status: 409 }
      );
    }

    // Verify the shift exists
    const shift = await prisma.shift.findUnique({
      where: { id: shiftId },
    });

    if (!shift) {
      return NextResponse.json(
        { error: 'Shift not found' },
        { status: 404 }
      );
    }

    const newTimesheet = await prisma.timesheet.create({
      data: {
        shiftId,
        status: TimesheetStatus.draft,
      },
    });

    return NextResponse.json({
      success: true,
      timesheet: newTimesheet,
    });
  } catch (error) {
    console.error('Error creating timesheet:', error);
    const errorMessage =
      error instanceof Error ? error.message : 'An unexpected error occurred';
    return NextResponse.json(
      { error: 'Internal server error', details: errorMessage },
      { status: 500 }
    );
  }
}
