import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/middleware';
import { prisma } from '@/lib/prisma';
import { Storage } from '@google-cloud/storage';
import { UserRole } from '@prisma/client';

// Configure Google Cloud Storage
const storage = new Storage();
const bucketName = process.env.GCS_AVATAR_BUCKET || 'your-gcs-bucket-name';

export async function POST(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    // Check if GCS is properly configured
    if (!bucketName || bucketName === 'your-gcs-bucket-name') {
      console.error('GCS_AVATAR_BUCKET environment variable not set.');
      return NextResponse.json({ error: 'Server configuration error' }, { status: 500 });
    }

    const user = await getCurrentUser(request);
    const { id } = await params;
    
    if (!user) {
      return NextResponse.json(
        { error: 'Authentication required' },
        { status: 401 }
      );
    }

    // Users can update their own avatar, admins can update any user's avatar
    if (user.id !== id && user.role !== UserRole.Admin) {
      return NextResponse.json(
        { error: 'Insufficient permissions' },
        { status: 403 }
      );
    }

    const data = await request.formData();
    const file: File | null = data.get('avatar') as unknown as File;

    if (!file) {
      return NextResponse.json({ error: 'No file provided' }, { status: 400 });
    }

    // Validate file type
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp'];
    if (!allowedTypes.includes(file.type)) {
      return NextResponse.json({ 
        error: 'Invalid file type. Please upload a JPEG, PNG, GIF, or WebP image.' 
      }, { status: 400 });
    }

    // Validate file size (5MB limit)
    const maxSize = 5 * 1024 * 1024; // 5MB
    if (file.size > maxSize) {
      return NextResponse.json({ 
        error: 'File too large. Please upload an image smaller than 5MB.' 
      }, { status: 400 });
    }

    const buffer = Buffer.from(await file.arrayBuffer());
    const fileName = `avatars/${id}-${Date.now()}-${file.name.replace(/[^a-zA-Z0-9.-]/g, '_')}`;
    
    console.log(`Uploading avatar to GCS bucket: ${bucketName}, file: ${fileName}`);
    
    const bucket = storage.bucket(bucketName);
    const blob = bucket.file(fileName);
    
    const blobStream = blob.createWriteStream({
      resumable: false,
      contentType: file.type,
      metadata: {
        cacheControl: 'public, max-age=31536000', // Cache for 1 year
      },
    });

    // Upload to Google Cloud Storage
    await new Promise((resolve, reject) => {
      blobStream.on('finish', resolve);
      blobStream.on('error', (error) => {
        console.error('GCS upload error:', error);
        reject(error);
      });
      blobStream.end(buffer);
    });

    // Make the file publicly accessible
    await blob.makePublic();

    const publicUrl = `https://storage.googleapis.com/${bucketName}/${fileName}`;
    console.log(`Avatar uploaded successfully: ${publicUrl}`);

    // Update user's avatar URL in database
    await prisma.user.update({
      where: { id },
      data: { avatarUrl: publicUrl },
    });

    return NextResponse.json({ success: true, url: publicUrl });
  } catch (error) {
    console.error('Error uploading avatar:', error);
    
    // Provide more specific error messages
    if (error instanceof Error) {
      if (error.message.includes('bucket does not exist')) {
        return NextResponse.json(
          { error: 'Storage bucket not found. Please contact support.' },
          { status: 500 }
        );
      }
      if (error.message.includes('permission')) {
        return NextResponse.json(
          { error: 'Storage permission denied. Please contact support.' },
          { status: 500 }
        );
      }
    }
    
    return NextResponse.json(
      { error: 'Failed to upload avatar. Please try again.' },
      { status: 500 }
    );
  }
}

