+const { PrismaClient } = require('@prisma/client');
+const { exec } = require('child_process');
+const fs = require('fs');
+
+async function updateSchema() {
+  const prisma = new PrismaClient();
+  
+  try {
+    console.log(' Connecting to database...');
+    await prisma.$connect();
+    
+    console.log(' Adding avatarData column...');
+    await prisma.$executeRaw`
+      DO $$ 
+      BEGIN
+          IF NOT EXISTS (SELECT 1 FROM information_schema.columns 
+                         WHERE table_name = 'User' AND column_name = 'avatarData') THEN
+              ALTER TABLE "User" ADD COLUMN "avatarData" TEXT;
+              RAISE NOTICE 'Column avatarData added successfully';
+          ELSE
+              RAISE NOTICE 'Column avatarData already exists';
+          END IF;
+      END $$;
+    `;
+    
+    console.log(' Database schema updated successfully');
+    
+    console.log(' Regenerating Prisma client...');
+    exec('npx prisma generate', (error, stdout, stderr) => {
+      if (error) {
+        console.error(' Error regenerating Prisma client:', error);
+        return;
+      }
+      console.log(' Prisma client regenerated successfully');
+      console.log(stdout);
+      if (stderr) console.log('Warnings:', stderr);
+    });
+    
+  } catch (error) {
+    console.error(' Error updating schema:', error);
+  } finally {
+    await prisma.$disconnect();
+  }
+}
+
+updateSchema();

