import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { parseDataUrl } from '@/lib/image-utils';

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    
    // Get user's avatar data from database
    const user = await prisma.user.findUnique({
      where: { id },
      select: { avatarUrl: true }
    });

    if (!user) {
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    // Check if avatarUrl contains base64 data (starts with data:)
    if (user.avatarUrl && user.avatarUrl.startsWith('data:')) {
      const imageInfo = parseDataUrl(user.avatarUrl);
      if (imageInfo) {
        const imageBuffer = Buffer.from(imageInfo.data, 'base64');
        
        // Generate ETag based on content hash for better caching
        const crypto = require('crypto');
        const hash = crypto.createHash('md5').update(imageBuffer).digest('hex');
        const etag = `"${hash}"`;
        
        // Check if client has cached version
        const clientETag = request.headers.get('if-none-match');
        if (clientETag === etag) {
          return new NextResponse(null, { status: 304 });
        }
        
        return new NextResponse(imageBuffer, {
          headers: {
            'Content-Type': imageInfo.mimeType,
            'Content-Length': imageBuffer.length.toString(),
            'Cache-Control': 'public, max-age=3600, must-revalidate', // Cache for 1 hour with revalidation
            'ETag': etag,
            'Last-Modified': new Date().toUTCString(),
            // CORS headers for cross-origin requests
            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Methods': 'GET',
            'Access-Control-Max-Age': '86400',
          }
        });
      }
    }

    // If user has old URL-based avatar, redirect to it
    if (user.avatarUrl && !user.avatarUrl.startsWith('data:')) {
      return NextResponse.redirect(user.avatarUrl);
    }

    // No avatar found
    return NextResponse.json({ error: 'No avatar found' }, { status: 404 });
    
  } catch (error) {
    console.error('Error serving avatar:', error);
    return NextResponse.json(
      { error: 'Failed to load avatar' },
      { status: 500 }
    );
  }
}

import { QueryClient } from '@tanstack/react-query';
import { apiService } from './api';

/**
 * Enhanced service for prefetching common data and managing cache
 */
export class PrefetchService {
  private queryClient: QueryClient;
  private prefetchQueue: Set<string> = new Set();
  private backgroundPrefetchInterval: NodeJS.Timeout | null = null;

  constructor(queryClient: QueryClient) {
    this.queryClient = queryClient;
    this.startBackgroundPrefetch();
  }

  /**
   * Prefetch shifts data with common filters
   */
  async prefetchShifts() {
    const commonFilters = [
      { date: 'today' },
      { date: 'tomorrow' },
      { date: 'this_week' },
      { status: 'Upcoming' },
      { status: 'In Progress' },
    ];

    const prefetchPromises = commonFilters.map(filters =>
      this.queryClient.prefetchQuery({
        queryKey: ['shifts', filters],
        queryFn: () => apiService.getShifts(filters),
        staleTime: 5 * 60 * 1000, // 5 minutes
      })
    );

    try {
      await Promise.allSettled(prefetchPromises);
      console.log(' Shifts data prefetched successfully');
    } catch (error) {
      console.warn(' Some shifts prefetch failed:', error);
    }
  }

  /**
   * Prefetch user data
   */
  async prefetchUsers() {
    try {
      await this.queryClient.prefetchQuery({
        queryKey: ['users', { page: 1, pageSize: 50 }],
        queryFn: () => apiService.getUsers({ page: 1, pageSize: 50 }),
        staleTime: 10 * 60 * 1000, // 10 minutes
      });
      console.log(' Users data prefetched successfully');
    } catch (error) {
      console.warn(' Users prefetch failed:', error);
    }
  }

  /**
   * Prefetch companies data
   */
  async prefetchCompanies() {
    try {
      await this.queryClient.prefetchQuery({
        queryKey: ['companies'],
        queryFn: () => apiService.getCompanies(),
        staleTime: 15 * 60 * 1000, // 15 minutes
      });
      console.log(' Companies data prefetched successfully');
    } catch (error) {
      console.warn(' Companies prefetch failed:', error);
    }
  }

  /**
   * Prefetch jobs data
   */
  async prefetchJobs() {
    try {
      await this.queryClient.prefetchQuery({
        queryKey: ['jobs', { page: 1, pageSize: 50 }],
        queryFn: () => apiService.getJobs({ page: 1, pageSize: 50 }),
        staleTime: 10 * 60 * 1000, // 10 minutes
      });
      console.log(' Jobs data prefetched successfully');
    } catch (error) {
      console.warn(' Jobs prefetch failed:', error);
    }
  }

  /**
   * Prefetch all common data
   */
  async prefetchAll() {
    console.log(' Starting data prefetch...');
    const startTime = Date.now();

    await Promise.allSettled([
      this.prefetchShifts(),
      this.prefetchUsers(),
      this.prefetchCompanies(),
      this.prefetchJobs(),
    ]);

    const duration = Date.now() - startTime;
    console.log(` Data prefetch completed in ${duration}ms`);
  }

  /**
   * Prefetch data based on user role
   */
  async prefetchForRole(role: string) {
    console.log(` Prefetching data for role: ${role}`);

    switch (role) {
      case 'Admin':
        await this.prefetchAll();
        break;
      case 'CrewChief':
        await Promise.allSettled([
          this.prefetchShifts(),
          this.prefetchUsers(),
        ]);
        break;
      case 'Staff':
        await this.prefetchShifts();
        break;
      default:
        await this.prefetchShifts();
    }
  }

  /**
   * Invalidate and refetch stale data
   */
  async refreshStaleData() {
    console.log(' Refreshing stale data...');
    
    // Invalidate queries older than 5 minutes
    await this.queryClient.invalidateQueries({
      predicate: (query) => {
        const lastUpdated = query.state.dataUpdatedAt;
        const fiveMinutesAgo = Date.now() - 5 * 60 * 1000;
        return lastUpdated < fiveMinutesAgo;
      }
    });
  }

  /**
   * Clear all cached data
   */
  clearCache() {
    console.log(' Clearing all cached data...');
    this.queryClient.clear();
  }

  /**
   * Get cache statistics
   */
  getCacheStats() {
    const cache = this.queryClient.getQueryCache();
    const queries = cache.getAll();
    
    const stats = {
      totalQueries: queries.length,
      freshQueries: queries.filter(q => q.state.status === 'success' && !q.isStale()).length,
      staleQueries: queries.filter(q => q.isStale()).length,
      errorQueries: queries.filter(q => q.state.status === 'error').length,
      loadingQueries: queries.filter(q => q.state.status === 'loading').length,
    };

    return stats;
  }
}

// Export a singleton instance
let prefetchService: PrefetchService | null = null;

export const getPrefetchService = (queryClient: QueryClient): PrefetchService => {
  if (!prefetchService) {
    prefetchService = new PrefetchService(queryClient);
  }
  return prefetchService;
};

