+      },
+      {
+        queryKey: ['shifts', { status: 'In Progress' }],
+        queryFn: () => apiService.getShifts({ status: 'In Progress' }),
+        staleTime: 2 * 60 * 1000,
+      },
+      {
+        queryKey: ['users', { role: 'Staff' }],
+        queryFn: () => apiService.getUsers({ role: 'Staff' }),
+        staleTime: 10 * 60 * 1000,
+      },
